var scriptExecution = Java.type('org.openhab.core.model.script.actions.ScriptExecution');

var zdt = Java.type('java.time.ZonedDateTime');

if (typeof this.timers === 'undefined') {
  this.timers = [];
}


if (typeof this.timers['ICDTimer'] === 'undefined' || this.timers['ICDTimer'].hasTerminated()) {
  this.timers['ICDTimer'] = scriptExecution.createTimer(zdt.now().plusSeconds(10), function () {
    events.sendCommand('ICD', 'OFF');
    })
}

